    /*   
    函数：格式化日期   
    参数：formatStr-格式化字符串   
    d：将日显示为不带前导零的数字，如1   
    dd：将日显示为带前导零的数字，如01   
    ddd：将日显示为缩写形式，如Sun   
    dddd：将日显示为全名，如Sunday   
    M：将月份显示为不带前导零的数字，如一月显示为1   
    MM：将月份显示为带前导零的数字，如01  
    MMM：将月份显示为缩写形式，如Jan  
    MMMM：将月份显示为完整月份名，如January  
    yy：以两位数字格式显示年份  
    yyyy：以四位数字格式显示年份  
    h：使用12小时制将小时显示为不带前导零的数字，注意||的用法  
    hh：使用12小时制将小时显示为带前导零的数字  
    H：使用24小时制将小时显示为不带前导零的数字  
    HH：使用24小时制将小时显示为带前导零的数字  
    m：将分钟显示为不带前导零的数字  
    mm：将分钟显示为带前导零的数字  
    s：将秒显示为不带前导零的数字  
    ss：将秒显示为带前导零的数字  
    l：将毫秒显示为不带前导零的数字  
    ll：将毫秒显示为带前导零的数字  
    tt：显示am/pm  
    TT：显示AM/PM  
    返回：格式化后的日期  
    */ 
    Date.prototype.format = function (formatStr) {  
        var _d = this;  
        //函数：补0
        var _z = function (value, length) {  
            if (!length) {  
                length = 2;  
            }  
            value = new String(value);  
            for (var i = 0, zeros = ''; i < (length - value.length); i++) {  
                zeros += '0';  
            }  
            return zeros + value;  
        };  
        return formatStr.replace(/"[^"]*"|'[^']*'|\b(?:d{1,4}|M{1,4}|yy(?:yy)?|([hHmstT])\1?|[lLZ])\b/g, function(_a) {  
            switch (_a) {  
                case 'd': return _d.getDate();  
                case 'dd': return _z(_d.getDate());  
                case 'ddd': return ['Sun', 'Mon', 'Tue', 'Wed', 'Thr', 'Fri', 'Sat'][_d.getDay()];  
                case 'dddd': return ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'][_d.getDay()];  
                case 'M': return _d.getMonth() + 1;  
                case 'MM': return _z(_d.getMonth() + 1);  
                case 'MMM': return ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'][_d.getMonth()];  
                case 'MMMM': return ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'][_d.getMonth()];  
                case 'yy': return new String(_d.getFullYear()).substr(2);  
                case 'yyyy': return _d.getFullYear();  
                case 'h': return _d.getHours() % 12 || 12;  
                case 'hh': return _z(_d.getHours() % 12 || 12);  
                case 'H': return _d.getHours();  
                case 'HH': return _z(_d.getHours());  
                case 'm': return _d.getMinutes();  
                case 'mm': return _z(_d.getMinutes());  
                case 's': return _d.getSeconds();  
                case 'ss': return _z(_d.getSeconds());  
                case 'l': return _d.getMilliseconds();  
                case 'll': return _z(_d.getMilliseconds());  
                case 'tt': return _d.getHours() < 12 ? 'am' : 'pm';  
                case 'TT': return _d.getHours() < 12 ? 'AM' : 'PM';  
            }  
        });
    };
    
